      var startingToRecord, timeSpan;
      startingToRecord = $recordButton.html() === "Record";
      $recordButton.html((startingToRecord ? "Stop" : "Record")).toggleClass("btn-success").toggleClass("btn-danger");
      if (typeof Storage === "undefined" || Storage === null) {
        return;
      }
      if (startingToRecord) {
        localStorage.setItem(currentUrl, $.now());
        $timerReading.text("0:00");
        beginIncrementer();
      } else {
        timeSpan = {
          interval: localStorage.getItem(currentUrl) + " " + $.now()
        };
        $.post(currentUrl + "/intervals-ajax.json", timeSpan, function(data) {
          $("#interval-table").append($("<tr>").addClass("interval").attr("id", data.id).append($('<td>').text(data.start), $('<td>').text(data.end), $('<td>').text(data.time), $('<td>').text(data.date), $('<td>').html(data.button.replace(/'/g, '"'))));
          cancelRecording();
        });
        localStorage.removeItem(currentUrl);
      }
